import timeimport randomcontent = ["Do you prefer a little spontaneity"," in life? Find our selection of online random ","quizzes on ‘random’ topics for you to ","choose from. Academics, fashion, history,"," sports, entertainment - name it, and we might just have it!"]def mistakes (user_input,test):  error = 0  for x in range(len(test)):    try:      if(test[x] != user_input[x]):        error = error + 1    except:      error = error + 1      pass  return error def speed_daley(time_s,time_e):  speed = str(round(round(time_e - time_s) % round(time_e)))  return speed  test = str(random.choice(content))print("     **** Speed Test ****  ")print(test)time_s = time.time()user_input = input("Enter word : ")time_e = time.time()print("Incorrect Word : ",mistakes(str(user_input),str(test)))print("Speed : ",speed_daley(time_s,time_e)," wp/sec")